const clientId = 'ed3ad78238e74a08a597d93c074d69dd'; 
const redirectUri = 'http://localhost:3000/';
let accessToken;

const Spotify = {
  getAccessToken() {
    if (accessToken) return accessToken;

    // Check if access token is in URL
    const tokenMatch = window.location.href.match(/access_token=([^&]*)/);
    const expiresInMatch = window.location.href.match(/expires_in=([^&]*)/);

    if (tokenMatch && expiresInMatch) {
      accessToken = tokenMatch[1];
      const expiresIn = Number(expiresInMatch[1]);

      // Clear the token after it expires
      window.setTimeout(() => accessToken = '', expiresIn * 1000);
      // Clean the URL
      window.history.pushState('Access Token', null, '/');

      return accessToken;
    } else {
      // Redirect user to Spotify auth
      const authUrl = 'https://accounts.spotify.com/authorize';
      const scope = 'playlist-modify-public';
      const responseType = 'token';

      const redirect = `${authUrl}?client_id=${clientId}&response_type=${responseType}&scope=${scope}&redirect_uri=${encodeURIComponent(redirectUri)}`;
      window.location = redirect;
    }
  },

  async search(query) {
    const token = Spotify.getAccessToken();
    const endpoint = `https://api.spotify.com/v1/search?type=track&q=${encodeURIComponent(query)}`;
    const response = await fetch(endpoint, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
  },


};


export default Spotify;
