 import React from 'react';
 import styles from "./Track.module.css";


 function Track({track, onAdd, isRemoval, onRemove}){


    const handleAdd = () => {
        if(onAdd){
            onAdd(track);
        }
    }
    const handleRemove = () => {
        if(onRemove){
            onRemove(track);
        }
    }

            return (
                <div className={styles.track}>
                    <h2 className={styles.trackInfo_name}>{track.name}</h2>
                    <div className={styles.trackInfo_lower}>
                        <p className={`${styles.trackInfo_details}`}>
                            {track.artist}
                        </p>
                        <p
                            className={`${styles.trackInfo_details} ${styles.trackInfo_spacer}`}
                        >
                            |
                        </p>
                        <p className={`${styles.trackInfo_details}`}>{track.album}</p>
                    </div>


      {!isRemoval &&  (
        <button className={styles.track_add} onClick={handleAdd}>➕ Add</button>
      )}
      {isRemoval && (
        <button onClick={handleRemove}>❌ Remove</button>
      )}
    </div>
  );
}


 export default Track;
