import React from "react";
import { useSelector } from "react-redux";
import { selectArticles, filterArticles } from "../features/articles/articlesSlice";
import Search from "./Search";
import { Link, useSearchParams } from "react-router-dom"; // ✅ Import useSearchParams

export default function Articles () {
  const articles = useSelector(selectArticles);

  const [searchParams] = useSearchParams(); // ✅ Grab URLSearchParams object
  const title = searchParams.get('title') || ''; // ✅ Get the title query param

  const filteredArticles = title ? filterArticles(title, articles) : Object.values(articles || {});

  return (
    <main>
      <h1>Articles</h1>
      <ul>
        { filteredArticles.map(article => (
          <li key={article.slug}>
            <Link to={`/articles/${article.slug}`}> {/* ✅ Use proper path */}
              {article.title}
            </Link>
          </li>
        ))}
      </ul>
      <Search />
    </main>
  );
}
