import React, {useState} from "react";

function Pad(props){
    const [on, setOn] = useState(props.on)

    return (
        <button 
            style={{backgroundColor: on ? props.color : "#ccc"}}
            className={on ? "on" : undefined}
            onClick={props.toggle()}
        ></button>
    )

}

export default Pad;
